SPDataList.c:CreateProcedureSQL:
create procedure BBDW.USR_USP_DATALIST_SUSTAINEDGIVINGREVENUE ( @STARTDATE datetime = null, @ENDDATE datetime = null ) as set nocount on;
declare @DATERANGE int set @DATERANGE = datediff(d,@STARTDATE, @ENDDATE);
if @DATERANGE > 700 or @DATERANGE is null
--Do Year begin
select d.[CALENDARYEAR] as [DATEFIELD], sum(rgi.[RECURRINGGIFTINSTALLMENTPAYMENTAMOUNT]) as [AMOUNT] from BBDW.[FACT_RECURRINGGIFTINSTALLMENTPAYMENT] rgi inner join BBDW.[FACT_FINANCIALTRANSACTION] ft on rgi.[FINANCIALTRANSACTIONFACTID] = ft.[FINANCIALTRANSACTIONFACTID] inner join BBDW.[DIM_DATE] d on ft.[FINANCIALTRANSACTIONDATEDIMID] = d.[DATEDIMID] and (@STARTDATE is null or ft.[FINANCIALTRANSACTIONDATE] > @STARTDATE) and (@ENDDATE is null or ft.[FINANCIALTRANSACTIONDATE] < @ENDDATE) group by d.[CALENDARYEAR] order by d.[CALENDARYEAR];
end
if @DATERANGE between 200 and 700
--Do Month begin
select d.[CALENDARMONTHYEARNAME] as [DATEFIELD], sum(rgi.[RECURRINGGIFTINSTALLMENTPAYMENTAMOUNT]) as [AMOUNT] from BBDW.[FACT_RECURRINGGIFTINSTALLMENTPAYMENT] rgi inner join BBDW.[FACT_FINANCIALTRANSACTION] ft on rgi.[FINANCIALTRANSACTIONFACTID] = ft.[FINANCIALTRANSACTIONFACTID] inner join BBDW.[DIM_DATE] d on ft.[FINANCIALTRANSACTIONDATEDIMID] = d.[DATEDIMID] and (@STARTDATE is null or ft.[FINANCIALTRANSACTIONDATE] > @STARTDATE) and (@ENDDATE is null or ft.[FINANCIALTRANSACTIONDATE] < @ENDDATE) group by d.[CALENDARMONTHYEARNAME], d.[CALENDARYEARMONTH] order by d.[CALENDARYEARMONTH];
end
if @DATERANGE between 32 and 200
--Do Week begin
select d.[CALENDARWEEKYEARNAME] as [DATEFIELD], sum(rgi.[RECURRINGGIFTINSTALLMENTPAYMENTAMOUNT]) as [AMOUNT] from BBDW.[FACT_RECURRINGGIFTINSTALLMENTPAYMENT] rgi inner join BBDW.[FACT_FINANCIALTRANSACTION] ft on rgi.[FINANCIALTRANSACTIONFACTID] = ft.[FINANCIALTRANSACTIONFACTID] inner join BBDW.[DIM_DATE] d on ft.[FINANCIALTRANSACTIONDATEDIMID] = d.[DATEDIMID] and (@STARTDATE is null or ft.[FINANCIALTRANSACTIONDATE] > @STARTDATE) and (@ENDDATE is null or ft.[FINANCIALTRANSACTIONDATE] < @ENDDATE) group by d.[CALENDARYEAR], d.[CALENDARWEEK], d.[CALENDARWEEKYEARNAME] order by d.[CALENDARYEAR], d.[CALENDARWEEK];
end
if @DATERANGE < 32
--Do Day begin
select d.[ACTUALDATESTRINGMDY] as [DATEFIELD], sum(rgi.[RECURRINGGIFTINSTALLMENTPAYMENTAMOUNT]) as [AMOUNT] from BBDW.[FACT_RECURRINGGIFTINSTALLMENTPAYMENT] rgi inner join BBDW.[FACT_FINANCIALTRANSACTION] ft on rgi.[FINANCIALTRANSACTIONFACTID] = ft.[FINANCIALTRANSACTIONFACTID] inner join BBDW.[DIM_DATE] d on ft.[FINANCIALTRANSACTIONDATEDIMID] = d.[DATEDIMID] and (@STARTDATE is null or ft.[FINANCIALTRANSACTIONDATE] > @STARTDATE) and (@ENDDATE is null or ft.[FINANCIALTRANSACTIONDATE] < @ENDDATE) group by d.[ACTUALDATESTRINGMDY], d.[ACTUALDATE] order by d.[ACTUALDATE];
end
